mapper.select:
select count(*)
from bbs
where isdelete = 'N'
	
		and category = #{category}
	
	
		and unum = #{unum}
	
	
		and id = #{id}
	
	
		and nickname = #{nickname}
	
	
		and isreport = #{isreport}
	
	
		and isopen = #{isopen}
	
	
		and (title like #{title} or content like #{content})
	
	
		and content like #{content}
	
	
		and title like #{title}
	
	
		or region like #{region}

select bnum, category, region, unum, id, nickname, DATE_FORMAT(create_date, '%Y-%m-%d') as createdate, title, content,
	   view_cnt, recommend, isdelete, isreport, isopen
from bbs
where isdelete = 'N'
	
		and category = #{category}
	
	
		and unum = #{unum}
	
	
		and id = #{id}
	
	
		and nickname = #{nickname}
	
	
		and isreport = #{isreport}
	
	
		and isopen = #{isopen}
	
	
		and (title like #{title} or content like #{content})
	
	
		and content like #{content}
	
	
		and title like #{title}
	
	
		or region like #{region}
	
order by create_date desc
limit #{start}, #{count}

mapper.insert:
insert into bbs(category, unum, id, nickname, title, content
, region
)
values(#{category}, #{unum}, #{id}, #{nickname}, #{title}, #{content}
, #{region}
)

mapper.update:
update bbs

	, title = #{title}
	, content = #{content}
	, region = #{region}

where bnum=#{bnum}
