mapper.select:
select count(*)
from letter
where

	
		rnick = #{rnick}
	
	
		snick = #{snick}

select lnum, rnick, snick, title, content, date, ischeck
from letter
where

	
		rnick = #{rnick}
	
	
		snick = #{snick}
	

order by date desc

	
		limit #{rstart},
	
	
		limit #{sstart},
	

#{count}

select lnum, rnick, snick, title, content, date, ischeck
from letter
where lnum = #{lnum}
